import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const CrudDemo = () => {
  const [persons, setPersons] = useState([]);

  useEffect(() => {
    fetch('https://example.com/api/persons')
      .then(response => response.json())
      .then(data => setPersons(data));
  }, []);

  return (
    <div>
      <Table persons={persons} />
    </div>
  );
};

const Table = ({ persons }) => (
  <table>
    <TableHeader />
    <tbody>
      {persons.map(person => (
        <TableRow key={person.id} person={person} />
      ))}
    </tbody>
  </table>
);

const TableHeader = () => {
  return (
    <thead>
      <tr>
        <th>ID</th>
        <th>First Name</th>
        <th>Last Name</th>
        <th>Email</th>
        <th>Title</th>
        <th>Actions</th>
      </tr>
    </thead>
  );
}

const TableRow = ({ person }) => (
  <tr>
    <td>{person.id}</td>
    <td>{person.name}</td>
    <td>{person.email}</td>
    <td>
      <TableActions personId={person.id} />
    </td>
  </tr>
);

const TableActions = ({ personId }) => (
  <>
    <Link to={`/edit/${personId}`}>Edit</Link>
    <Link to={`/delete/${personId}`}>Delete</Link>
  </>
);

export default CrudDemo;
