import React from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

import DemoRouter from "./Components/DemoRouter";
import CrudDemo from "./Components/CrudDemo";
import PersonDetails from "./Components/PersonDetails";
import UpdatePerson from "./Components/UpdatePerson";

const App = () => {
  return (
    <Router>
      <div>
        <DemoRouter />
        <Switch>
          <Route exact path="/" component={CrudDemo} />
          <Route path="/person/:id" component={PersonDetails} />
          <Route path="/update/:id" component={UpdatePerson} />
          <Route component={NotFound} />
        </Switch>
      </div>
    </Router>
  );
};
const Welcome = () => {
  return <h1>Welcome to Demo Router</h1>
}
const About = () => {
  return <h1>About Us</h1>
}
const Home = () => {
  return <h1>Home</h1>
}
const Person = (props) => {
  return <h1>Person: {props.match.params.id}</h1>
}
const NotFound = () => {
  return <h1>404: Page Not Found</h1>
}


export default App;
