import React, { useState, useEffect } from 'react';

const TableRow = ({ person, updatePersons }) => {
  const handleDelete = (id) => {
    fetch(`https://example.com/api/persons/${id}`, {
      method: 'DELETE'
    })
      .then(response => {
        if(response.ok) {
          updatePersons(prevPersons => prevPersons.filter(p => p.id !== id));
        }
      })
      .catch(error => console.log(error));
  }
  return (
    <tr>
      <td>{person.id}</td>
      <td>{person.name}</td>
      <td>{person.email}</td>
      <td>
        <TableActions personId={person.id} />
        <button onClick={() => handleDelete(person.id)}>Delete</button>
      </td>
    </tr>
  );
};

const Table = ({ persons, updatePersons }) => (
  <table>
    <TableHeader />
    <tbody>
      {persons.map(person => (
        <TableRow key={person.id} person={person} updatePersons={updatePersons} />
      ))}
    </tbody>
  </table>
);

const CrudDemo = () => {
  const [persons, setPersons] = useState([]);

  useEffect(() => {
    fetch('https://example.com/api/persons')
      .then(response => response.json())
      .then(data => setPersons(data));
  }, []);

  return (
    <div>
      <Table persons={persons} updatePersons={setPersons} />
    </div>
  );
};
