import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';

const TableRow = ({ person }) => {
  const history = useHistory();
  const handleClick = (id) => {
    history.push(`/person/${id}`);
  }
  return (
    <tr>
      <td>{person.id}</td>
      <td>{person.name}</td>
      <td>{person.email}</td>
      <td>
        <TableActions personId={person.id} />
        <button onClick={() => handleClick(person.id)}>Details</button>
      </td>
    </tr>
  );
};

const PersonDetails = () => {
  const [person, setPerson] = useState({});
  const { id } = useParams();

  useEffect(() => {
    fetch(`https://example.com/api/persons/${id}`)
      .then(response => response.json())
      .then(data => setPerson(data));
  }, [id]);

  return (
    <div>
      <h1>Person Details</h1>
      <p>Name: {person.name}</p>
      <p>Email: {person.email}</p>
    </div>
  );
};

// In the DemoRouter component
<Route path="/person/:id" component={PersonDetails} />
